package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int fact=1;  
		 int test=5;
		   
		  for(int i=1;i<=test;i++){    
			  fact=fact*i;    
		  }    
		  System.out.println("Factorial = "+fact);    
		 }  
		

	}


